USE nyc_taxi_db;

SELECT min(fare_amount) as min_fare_amount,
        max(fare_amount) as max_fare_amount,
        avg(fare_amount) as avg_fare_amount
FROM 
OPENROWSET(
    BULK 'trip_data_green_parquet/year=*/month=*/',
    DATA_SOURCE = 'nyc_taxi_data_raw',
    FORMAT = 'parquet'
) as trip_data;
